import React, {useState} from 'react'

const Blog = ({title, author, likes, url, user }) => {
  const [opened, setOpened] = useState(false)
  const hideWhenOpened = { display: opened ? 'none' : '' }
  const showWhenOpened = { display: opened ? '' : 'none' }

  const toggleOpened = () => {
    setOpened(!opened)
  }

  return(
    <div>
    <div style={hideWhenOpened}>
      <div className="blog" onClick={toggleOpened}> {title} {author}</div>
    </div>
    <div style={showWhenOpened}>
       <div className="blog">
          <div>
            <div  onClick={toggleOpened}>{title} {author}</div><br/>
            {url} <br/>
            {likes} likes <button type="submit">like</button><br/>
            added by {user.name}
          </div>
        </div>
    </div>
  </div>
)}

export default Blog
