import blogService from '../services/blogs'

export const likeBlog = (blogToLike) => {
  return async dispatch => {
    const content = { 
      ...blogToLike, 
      likes: blogToLike.likes +1 
    }
    const updatedBlog = await blogService.update(content)
    dispatch({
      type: 'LIKE_BLOG',
      data: updatedBlog
    })
  }
}

export const deleteBlog = (blogId) => {
  return async dispatch => {
    await blogService.deleteBlog(blogId)
    dispatch({
      type: 'DELETE_BLOG',
      data: blogId
    })
  }
}

export const createBlog = (content) => {
  return async dispatch => {
    const newBlog = await blogService.create(content)
    dispatch({
      type: 'NEW_BLOG',
      data: newBlog,
    })
  }
}

export const initializeBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      data: blogs,
    })
  }
}

const reducer = (state = [], action) => {
  console.log('state before action in blogReducer: ', state)
  console.log('action in blogReducer', action)

  switch(action.type) {
    case 'NEW_BLOG':
      return [...state, action.data]
    case 'LIKE_BLOG':
      const id = action.data.id
      return state.map(blog =>
        blog.id !== id ? blog : action.data 
      )
    case 'DELETE_BLOG':
      const deletedId = action.data.id
      return state.filter(blog => 
        blog.id !== deletedId
      )
    case 'INIT_BLOGS':
      return action.data
    default:
      return state
  }
}

export default reducer
