import React, {useEffect} from 'react'
import Blog from './Blog'
import { fetchBlogs } from '../reducers/blogReducer'
import { connect } from 'react-redux'


const Blogs = (props) => {
  useEffect(() => {
    props.fetchBlogs(props.user.token)
  }, [props.user])

  // const filteredBlogs = blogs.filter(blog => blog.author.toUpperCase().indexOf(filterValue.toUpperCase()) >= 0)
  console.log("props.blogs: ",props.blogs)
  if(props.blogs !== null){
    const sortedBlogs = props.blogs.sort((a,b) => b.likes - a.likes )
    const blogList = sortedBlogs.map(blog =>
      <Blog
        key={ blog.id }
        id={ blog.id }
        title={ blog.title }
        author={ blog.author }
        likes={ blog.likes }
        url={ blog.url }
        user={ blog.user }
      />
    )
    if(props.user.user !== null){
      return(
        <div className="blogList">{blogList}</div>
      )
    }
  } 
  return(<div/>)
}


const mapStateToProps = (state) => {
  return {
    blogs: state.blogs,
    user: state.user
  }
}

const mapDispatchToProps = {
  fetchBlogs,
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Blogs)
